import { Component } from '@angular/core';
import { WeatherService } from './services/weather.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public citySearch: string = '';
  public city: any;

  constructor(
    private weatherService: WeatherService
  ) { }

  ngOnInit() {

  }

  searchCity() {
    this.weatherService.getWeatherForCity(this.citySearch).subscribe({
      next: (data: any) => {
        console.log(data);
        data.weather[0].iconUrl = this.weatherService.getWeatherIcon(data.weather[0].icon);
        this.city = data;
      },
      error: (error) => {
        alert(error.error.message);
      }
    });
  }

}
<div class="header">
  <h1>Weather Search</h1>
</div>

<div class="container pt-5">
  <div class="form-floating">
    <div class="input-group mb-3">
      <input [(ngModel)]="citySearch" type="text" list="optionsList" id="searchInput" class="form-control" placeholder="Enter city name" (keyup.enter)="searchCity()" />
      <button class="btn btn-outline-secondary" type="button" id="searchButton" (click)="searchCity()">Search</button>
    </div>
    <label for="searchInput">Search a city</label>
  </div>
</div>

<div class="container pt-3" *ngIf="city">
  <p>City: {{city.name}}</p>
  <p>Country: {{city.sys.country}}</p>
  <img [src]="city.weather[0].iconUrl" alt="weather icon" />
  <p>Temperature: {{city.main.temp}} °C </p>
  <p>Humidity: {{city.main.humidity}}%</p>
  <p>Wind speed: {{city.wind.speed}} m/s</p>
</div>
