import { Component } from '@angular/core';
import { WeatherService } from './services/weather.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public citySearch: string = '';
  public cities: any[] = [];

  constructor(
    private weatherService: WeatherService
  ) { }

  ngOnInit() {

  }

  searchCity(citySearch: string) {
    this.weatherService.getCities(citySearch).subscribe({
      next: (data: any[]) => {
        console.log(data);
        this.cities = data;
      },
      error: (error) => {
        alert(error.message);
      }
    });
  }

}
<div class="header">
  <h1>Weather Search</h1>
</div>

<div class="container pt-5">
  <div class="form-group">
    <label for="searchInput">Search a city</label>
    <div class="input-group mb-3">
      <input [(ngModel)]="citySearch" type="text" list="optionsList" id="searchInput" class="form-control" placeholder="Enter city name" />
      <button class="btn btn-outline-secondary" type="button" id="searchButton" (click)="searchCity(citySearch)">Search</button>
    </div>
    <datalist id="optionsList">
      <option *ngFor="let city of cities">{{city.name}}</option>
    </datalist>
  </div>
</div>

<div class="container pt-3">
  <p>City:</p>
  <p>Country:</p>
  <p>Temperature:</p>
  <p>Humidity:</p>
  <p>Wind speed:</p>
</div>
