import { Component } from '@angular/core';
import { WeatherService } from './services/weather.service';
import * as countries_json from '../assets/countries.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public citySearch: string = '';
  public city: any;
  public countries = (countries_json as any).default;

  constructor(
    private weatherService: WeatherService
  ) { }

  ngOnInit() {

  }

  searchCity() {
    this.weatherService.getWeatherForCity(this.citySearch).subscribe({
      next: (data: any) => {
        data.weather[0].iconUrl = this.weatherService.getWeatherIcon(data.weather[0].icon);
        data.countryName = this.getCountryName(data.sys.country);
        data.time = this.getCountryTime(data.timezone);
        this.city = data;
      },
      error: (error) => {
        alert(error.error.message);
      }
    });
  }

  getCountryName(code: string) {
    const country = this.countries.find((country: any) => country.code === code);
    return country ? country.name : code;
  }

  getCountryTime(timezone: number) {
    const timeNow = new Date();
    const time = new Date(timeNow.getTime() + (timezone * 1000));
    const timeString = time.toLocaleTimeString();
    const dateString = time.toLocaleDateString();
    return timeString + ', ' + dateString;
  }

}
<div class="header">
  <h1>City Weather Search</h1>
</div>

<div class="container pt-5">
  <div class="input-group mb-3">
    <div class="form-floating">
      <input [(ngModel)]="citySearch" type="text" list="optionsList" id="searchInput" class="form-control" (keyup.enter)="searchCity()" />
      <label for="searchInput">Enter city name</label>
    </div>
    <button class="btn btn-outline-secondary" type="button" id="searchButton" (click)="searchCity()">Search</button>
  </div>

  <div class="row" *ngIf="city">
    <div class="col-6 text-end">
      <h2>{{city.name}}</h2>
      <p><img class="flag" src="https://flagcdn.com/48x36/{{city.sys.country.toLowerCase()}}.png"> {{city.countryName}}</p>
      <p>Local time {{city.time}}</p>
    </div>
    <div class="col-6">
      <app-weather-card class="weather-card" [city]="city"></app-weather-card>
    </div>
  </div>
</div>
